doi.map:
function(doc) {
       if (doc.identifier)
       {
          for (var i in doc.identifier)
          {
            if (doc.identifier[i].type == "doi")
            {
              emit(doc.identifier[i].id, doc._id);
            }
          }
       }


}

pmid.map:
function(doc) {
       if (doc.identifier)
       {
          for (var i in doc.identifier)
          {
            if (doc.identifier[i].type == "pmid")
            {
              emit(parseInt(doc.identifier[i].id), doc._id);
            }
          }
       }


}

citation.map:
function(doc) {
 if (doc.title)
{
  var citation = '';

  if (doc.author)
  {
    for (var i in doc.author)
    {
      var author = '';
      if (doc.author[i].lastname)
      {
        author += doc.author[i].lastname;
      }
      if (doc.author[i].forename)
      {
        author += ', ' + doc.author[i].forename;
      }
      if (author != '')
      {
        citation += author + '; ';
      }
    }
  }





  if (doc.year)
  {
    citation += '(' + doc.year + ') ';
  }

  citation += doc.title;
  if (doc.journal)
  {
    citation += '. ' + doc.journal.name;
    if (doc.journal.volume)
    {
      citation += ' ' + doc.journal.volume;
    }
   if (doc.journal.issue)
   {
      citation += '(' + doc.journal.issue + ')';
    }
   if (doc.journal.pages)
   {
      citation += ':' + doc.journal.pages.replace(/--/, '-');
    }
  }
  emit(doc._id, citation);

}


}

